	public Date convertStringToDate(String string) {
		if (string == null) {
			return null;
		}
		try {
			return DATETIME_FORMAT.parse(string);
		} catch (ParseException e) {
			System.out.println("execption:"+e);
			return null;
		}
	}
	
	
	
	public String getRejectClaimByCustomerName() throws Exception {
		
		employee=super.getEmployee();
		emplID=employee.getEmployeeId();
		emplName=employee.getRoleName();
		empRoleAcc=employee.getRoleAccess();
		if(orderID!=null && !orderID.equalsIgnoreCase(""))
		{
			orderID=orderID.trim();
		}
		if(claimID!=null && !claimID.equalsIgnoreCase(""))
		{
			claimID=claimID.trim();
		}
		if(customerInfo!=null && !customerInfo.equalsIgnoreCase(""))
		{
			customerInfo=customerInfo.trim();
		}
		if(claimItemId!=null && !claimItemId.equalsIgnoreCase(""))
		{
			claimItemId=claimItemId.trim();
		}
		List<Integer> divIds = getEmpMrchAssc(employee);
		/*
		 * if(customerInfo!=null && customerInfo.length()>0){ String
		 * tempCustomerInfo="%"+customerInfo+"%"; customerInfo=tempCustomerInfo;
		 * }
		 */

		List<ClaimDetailTable> rejectClaimList = new ArrayList<ClaimDetailTable>();
		ClaimDetailTable[] newRejectClaimList = null;
		OrderTransactionsIfc trans = DKartContext.getLookupOrder();
		String actionName = ActionContext.getContext().getName();
		
		
		if((datepicker3!= null && !datepicker3.equalsIgnoreCase(""))||(datepicker4!= null && !datepicker4.equalsIgnoreCase(""))||
		   (ClaimTotalFrom!= null && !ClaimTotalFrom.equalsIgnoreCase(""))||(ClaimTotalTo!= null && !ClaimTotalTo.equalsIgnoreCase(""))){
			if(datepicker3!= null && !datepicker3.equalsIgnoreCase("")){
				parsed_from = convertStringToDate(datepicker3);
			}
			if(datepicker4!= null && !datepicker4.equalsIgnoreCase("")){
				parsed_to = convertStringToDate(datepicker4);
			}
				//	In claim Search Claim tab @ jagadish
			if (actionName.equalsIgnoreCase("rejectClaimByCustomerName")){
					rejectClaimList = trans.getRejectedClaimSearchByClaim("6" ,empRoleAcc,divIds,emplID,claimID,parsed_from,parsed_to,ClaimTotalFrom,ClaimTotalTo,getItemId(claimItemId));
				
				// 	In Reject claim Search Claim tab @ jagadish	
			} else if (actionName.equalsIgnoreCase("allClaimByCustomerName")) {
					rejectClaimList = trans.getRjectedClaimDateAndTotalDetails(null,emplName,divIds,emplID,claimID,parsed_from,parsed_to,ClaimTotalFrom,ClaimTotalTo,getItemId(claimItemId),empRoleAcc);
			}
			
		}else if((datepicker1!= null && !datepicker1.equalsIgnoreCase(""))||(datepicker2!= null && !datepicker2.equalsIgnoreCase(""))||
				 (order_total_from!= null && !order_total_from.equalsIgnoreCase(""))||(order_total_to!= null && !order_total_to.equalsIgnoreCase(""))){
			if(datepicker1!= null && !datepicker1.equalsIgnoreCase("")){
				parsed_from = convertStringToDate(datepicker1);
			}
			if(datepicker2!= null && !datepicker2.equalsIgnoreCase("")){
				parsed_to = convertStringToDate(datepicker2);
			}
				 //  In Reject claim Search Order tab @ jagadish	
			if (actionName.equalsIgnoreCase("rejectClaimByCustomerName")){   
					rejectClaimList = trans.getRejectedClaimSearchByOrder("6" ,empRoleAcc,divIds,emplID,orderID,parsed_from,parsed_to,order_total_from,order_total_to,getItemId(claimItemId));
				
				 //  In claim Search Order tab @ jagadish
			} else if (actionName.equalsIgnoreCase("allClaimByCustomerName")) {
					rejectClaimList = trans.getRjectedClaimOrderDetails(null,empRoleAcc,emplName,divIds,emplID,orderID,parsed_from,parsed_to,order_total_from,order_total_to);
			}
			
		} else {
				//	In Reject claim Search Customer tab @ jagadish
			if (actionName.equalsIgnoreCase("rejectClaimByCustomerName")){
				rejectClaimList = trans.getRejectedClaimSearchByCustomerInfo("6",empRoleAcc,divIds,emplID,orderID, claimID, customerInfo,getItemId(claimItemId));
				
				// 	In claim Search Customer tab @ jagadish
			} else if (actionName.equalsIgnoreCase("allClaimByCustomerName")) {
				rejectClaimList = trans.getRjectedClaimTableDetails(null,emplName,divIds,emplID, orderID, claimID, customerInfo,getItemId(claimItemId),empRoleAcc);
			} 
		}
		
		
		int count = 0;

		if (rejectClaimList != null && rejectClaimList.size() > 0) {
			for (int i = 0; i < rejectClaimList.size(); i++) {
				if (rejectClaimList.get(i) != null) {
					count = count + 1;
				}
			}

			newRejectClaimList = new ClaimDetailTable[count];
			for (int j = 0; j < count; j++) {

				newRejectClaimList[j] = rejectClaimList.get(j);
			}

		} else {
			newRejectClaimList = new ClaimDetailTable[count];
			addActionError("NOClaimsFnd");
		}
		setRejecClaimList(newRejectClaimList);
		setRange(DKartContext.getParamterBean().fetchXMLParameterValues().getClaimSearchRange());
		setRejectClaimRange(DKartContext.getParamterBean().fetchXMLParameterValues().getRejectedClaimRange());
		return SUCCESS;

	}

	private List<Integer> getEmpMrchAssc(EmployeeIfc employee2) {
		
			List<EmpMerchAssociationIfc> empMrchAss = employee.getMerchAssoc();
			List<Integer> divIds = new ArrayList<Integer>();
			if (empMrchAss != null) {
				for (EmpMerchAssociationIfc empMrchAsc : empMrchAss) {
					if (empMrchAsc.getMerchId().startsWith("1:")) {
						int merchId = Integer.parseInt(empMrchAsc.getMerchId().split("1:")[1]);
						divIds.add(merchId);
					}
				}
			}
			return divIds;
	}

