	private String getItemId(String claimItemId) {

		String itemID = null;
		if(claimItemId!=null && !claimItemId.equalsIgnoreCase(""))
		{
			if (claimItemId.contains("---")) {
				String[] itm = claimItemId.split("---");

				try {
					itemID = itm[1];
				} catch (Exception e) {
					System.out.println(e);
					itemID = itm[0];
				}
			} else {
				itemID = claimItemId;
			}
		}
		return itemID;
	}

