package rispl.ds.order;

import java.math.BigDecimal;
import java.text.DecimalFormat;
import java.util.LinkedHashMap;

import org.apache.logging.log4j.LogManager;
import org.apache.logging.log4j.Logger;

import rispl.dk.Employee.EmployeeIfc;
import rispl.dk.customer.CustomerIfc;
import rispl.dkart.services.ejb.transaction.OrderTransactionsIfc;
import rispl.dkart.services.entities.transaction.OrderTranHeader;
import rispl.ds.DSAction;
import rispl.ds.context.DKartContext;
import util.email.ConfirmOrderEmail;

public class ConfirmSaleOrderAction extends DSAction {

	/**
	 * @author Krishna
	 */
	private static final long serialVersionUID = 1L;
	static final Logger LOGGER = LogManager.getLogger(ConfirmSaleOrderAction.class);

	private OrderTranHeader orderTran;
	private String orderHeadStatus;

	private String invResult = null;
	private LinkedHashMap<String, String> inventoryCheckMap;

	
	public LinkedHashMap<String, String> getInventoryCheckMap() {
		return inventoryCheckMap;
	}

	public void setInventoryCheckMap(LinkedHashMap<String, String> inventoryCheckMap) {
		this.inventoryCheckMap = inventoryCheckMap;
	}

	public String getInvResult() {
		return invResult;
	}

	public void setInvResult(String invResult) {
		this.invResult = invResult;
	}

	public String validateSalesOrder() {
		orderTran = (OrderTranHeader) getFromSession(SESSION.QUOTE_DETAILS);
		if (orderTran != null) {
			//Validate Credit Limit
			CustomerIfc customer = orderTran.getCustomer();
			if (customer == null)
				return INPUT;

			BigDecimal transTotal = orderTran.getOrdTranSum().getDkartNetTot();
			DecimalFormat df = new DecimalFormat(getText("format.currency"));
			String actionErrorString = "Available Credit Limit is low. "
					+ "Manager override is required to continue with order confirm.";
			String fieldErrorCreditLimit = "creditLimit";
			String fieldErrorTransTotal = "transTotal";
			if (orderTran.getCreditLimitOverride() != null) {
				BigDecimal creditLimit = orderTran.getCreditLimitOverride();
				//BigDecimal transTotal = orderTran.getOrdTranSum().getDkartNetTot();
				BigDecimal avCreditLimit = creditLimit.subtract(transTotal);
				if (avCreditLimit.compareTo(BigDecimal.ZERO) < 0) {
					//DecimalFormat df=new DecimalFormat(getText("format.currency"));
					addActionError(actionErrorString);
					addFieldError(fieldErrorCreditLimit, df.format(creditLimit));
					addFieldError(fieldErrorTransTotal, df.format(transTotal));
					return ERROR;
				}
			} else if (orderTran.getTransCrdtLimit() != null) {
				BigDecimal creditLimit = orderTran.getTransCrdtLimit();
				//BigDecimal transTotal = orderTran.getOrdTranSum().getDkartNetTot();
				BigDecimal avCreditLimit = creditLimit.subtract(transTotal);
				if (avCreditLimit.compareTo(BigDecimal.ZERO) < 0) {
					//DecimalFormat df=new DecimalFormat(getText("format.currency"));
					addActionError(actionErrorString);
					addFieldError(fieldErrorCreditLimit, df.format(creditLimit));
					addFieldError(fieldErrorTransTotal, df.format(transTotal));
					return ERROR;
				}
			} else if (orderTran.getCustomer().getCustomerLimits().getAvCrdtLimit() != null) {
				BigDecimal creditLimit = orderTran.getCustomer().getCustomerLimits().getAvCrdtLimit();
				BigDecimal avCreditLimit = creditLimit.subtract(transTotal);
				if (avCreditLimit.compareTo(BigDecimal.ZERO) < 0) {
					//DecimalFormat df=new DecimalFormat(getText("format.currency"));
					addActionError(actionErrorString);
					addFieldError(fieldErrorCreditLimit, df.format(creditLimit));
					addFieldError(fieldErrorTransTotal, df.format(transTotal));
					return ERROR;
				}
			} else {
				addActionError("Exception during credit limit validation");
				addFieldError(fieldErrorCreditLimit, df.format(BigDecimal.ZERO));
				addFieldError(fieldErrorTransTotal, df.format(transTotal));
				return ERROR;
			}

			//Validate inventory
			{

			}

			return SUCCESS;
		}

		return INPUT;
	}

	@Override
	public String execute() {
		//String result = null;
		String validation = validateSalesOrder();
		
		String status = ERROR;
		orderTran = (OrderTranHeader)getFromSession(SESSION.QUOTE_DETAILS);
		if (orderTran != null && validation.equalsIgnoreCase(SUCCESS)) {
			try {

				OrderTransactionsIfc ordTrn = DKartContext.getLookupOrder();
				orderTran.setTransactionStatus(new BigDecimal(2));//transaction completed
				if (ordTrn.saveOrderTransaction(orderTran)) {
					status = SUCCESS;
					clearSessionData();
					setOrderHeadStatus("Order Placed");
					new ConfirmOrderEmail().sendConfirmOrderEmail(orderTran);
					/*new Thread(new Runnable() {
					    @Override
					    public void run() {
					    	new ConfirmOrderEmail().sendConfirmOrderEmail(orderTran);		
					    }
					}).start();*/
				}
			} catch (Exception e) {
				e.printStackTrace();
				LOGGER.error("", e);
			}

		}else if(orderTran != null && !validation.equalsIgnoreCase(SUCCESS)){
			addActionError("Available credit limit is not sufficient to complete the order");
			setOrderHeadStatus("Error");
		} 
		else {
			status = "home";
		}
		if (status.equalsIgnoreCase(ERROR)) {
			addActionMessage("Unknown Error Occur Contact Administrator");
			setOrderHeadStatus("Error");
		}
		return status;

	}

	//for validating inventory
	public LinkedHashMap<String, String> validateInventory(OrderTranHeader head) throws Exception {
		OrderTransactionsIfc ordTrn = DKartContext.getLookupOrder();
		return ordTrn.validateInventory(head);
	}

	public String cancelSaleOrder() {
		String result = NONE;
		int order = 0;
		orderTran = (OrderTranHeader) getFromSession(SESSION.ORDER_TRANSACTION);
		if(orderTran==null)
		{
			orderTran = (OrderTranHeader) getFromSession(SESSION.QUOTE_DETAILS);
			order = 1;
		}
		if (orderTran != null) {

			try {
				OrderTransactionsIfc ordTrn = DKartContext.getLookupOrder();
				EmployeeIfc employee = getEmployee();
				//orderTran.setEmId(employee.getEmployeeId());
				orderTran.setIdOpr(employee.getEmployeeId());
				if(orderTran.getOrdTy().equals("23") && orderTran.getTransactionStatus().compareTo(new BigDecimal(2))==0){
					orderTran.setOrdTy("25"); //Order canceled
					orderTran.setScOrd(new BigDecimal(9));//new order
				}else{
					orderTran.setOrdTy("3"); //Order Void
					orderTran.setScOrd(new BigDecimal(0));
				}
				orderTran.setTransactionStatus(new BigDecimal(2));//transaction
				if (ordTrn.saveOrderTransaction(orderTran)) {
					result = SUCCESS;
					//clearSessionData();
					if(order==0) orderTran = (OrderTranHeader)removeFromSession(SESSION.ORDER_TRANSACTION);
					else orderTran = (OrderTranHeader)removeFromSession(SESSION.QUOTE_DETAILS);
					setOrderHeadStatus("Order Cancelled");

				}
			} catch (Exception e) {
				e.printStackTrace();
				LOGGER.error("", e);
			}

		} else {
			result = "home";
		}

		if (result.equalsIgnoreCase(NONE)) {
			addActionMessage("An Unknown Error Occur Contact Administrator");
			setOrderHeadStatus("Error");
		}
		return result;

	}

	public String suspendSalesOrder() {
		clearSessionData();
		setOrderHeadStatus("Order Saved");
		return SUCCESS;
	}

	public void clearSessionData() {
		if (sessionContains(SESSION.QUOTE_DETAILS)) {
			orderTran = (OrderTranHeader) removeFromSession(SESSION.QUOTE_DETAILS);
			//getSessionmap().remove(SESSION.ORDER_TRANSACTION.toString());

		} else {
			LOGGER.error("Session value is null, so can not clear session data");
		}

	}

	public OrderTranHeader getOrderTran() {
		return orderTran;
	}

	public void setOrderTran(OrderTranHeader orderTran) {
		this.orderTran = orderTran;
	}

	public String getOrderHeadStatus() {
		return orderHeadStatus;
	}

	public void setOrderHeadStatus(String orderHeadStatus) {
		this.orderHeadStatus = orderHeadStatus;
	}

	public String checkInventory() {
		if(!getValidateInventory()){
			this.setInvResult("proceed");
			return SUCCESS;
		}
			
		//begin
		LinkedHashMap<String, String> result = null;
		this.setInvResult(null);
		setInventoryCheckMap(null);
		orderTran = (OrderTranHeader)getFromSession(SESSION.QUOTE_DETAILS);
		try {
			result = validateInventory(orderTran);
		} catch (Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		if (result != null) {
			if (result.size() != 0) {
				setInventoryCheckMap(result);
				this.setInvResult("");

			} else {
				this.setInvResult("proceed");
			}

		} else {
			this.setInvResult("fail");

		}
		//end
		return SUCCESS;
	}
	
	public boolean getValidateInventory() {
		try {
			return DKartContext.getParamterBean().fetchXMLParameterValues().getEnableValidateInventory()
					.equalsIgnoreCase("Yes") ? true : false;
		} catch (Exception e) {
		}
		return true;
	}
	
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     